mapper.select:



        AND `name` like concat('%', #{name}, '%')


        AND hidden = #{hidden}


        AND status = #{status}


order by parent_id, order_num

select distinct m.id, m.parent_id, m.name, m.path, m.component, m.`query`, m.hidden, ifnull(m.perms,'') as perms, m.is_frame, m.is_cache, m.type, m.icon, m.sequence, m.create_time from gy_sys_menu m left join gy_sys_role_menu rm on m.id = rm.menu_id left join gy_sys_user_role ur on rm.role_id = ur.role_id left join gy_role ro on ur.role_id = ro.id where ur.user_id = #{params.userId}
AND m.name like concat('%', #{name}, '%')
AND m.hidden = #{hidden}
order by m.parent_id, m.sequence

select m.id from gy_sys_menu m left join gy_sys_role_menu rm on m.id = rm.menu_id where rm.role_id = #{roleId}
and m.menu_id not in (select m.parent_id from sys_menu m inner join sys_role_menu rm on m.menu_id = rm.menu_id and rm.role_id = #{roleId})
order by m.parent_id, m.order_num

select count(1)
from sys_menu
where parent_id = #{menuId}

mapper.update:
update sys_menu
menu_name = #{menuName}, parent_id = #{parentId}, order_num = #{orderNum}, path = #{path}, component = #{component}, `query` = #{query}, is_frame = #{isFrame}, is_cache = #{isCache}, menu_type = #{menuType}, visible = #{visible}, status = #{status}, perms = #{perms}, icon = #{icon}, remark = #{remark}, update_by = #{updateBy}, update_time = sysdate()
where menu_id = #{menuId}
